function currentPage(){let t=window.location.pathname.split("/").pop()||"index.html";document.querySelectorAll(".sidebar__link, .dropdown__link").forEach(e=>{if(e.getAttribute("href")===t){e.classList.add("active");let t=e.closest(".dropdown__content");for(;t;){var o=t.closest(".dropdown");if(!o)break;var r=o.querySelector(".dropdown__title");r&&r.classList.add("active-title"),o.classList.add("dropdown--active"),t=o.closest(".dropdown__content")}}})}export{currentPage};