function initializeCounters(){let t=new IntersectionObserver(onSectionIntersect,{threshold:.3});document.querySelectorAll(".page_section").forEach(e=>t.observe(e))}function onSectionIntersect(e,t){e.forEach(e=>{e.isIntersecting&&(e.target.querySelectorAll("[data-counter-target]").forEach(e=>animateCounter(e)),t.unobserve(e.target))})}function animateCounter(n){let r=parseInt(n.dataset.counterTarget)||0,o=parseInt(n.textContent)||0,a=performance.now();requestAnimationFrame(function e(t){t-=a,t=Math.min(t/1e3,1);n.textContent=Math.floor(t*(r-o)+o),t<1&&requestAnimationFrame(e)})}export{initializeCounters};